% Read the image
image=imread('data:image/jpeg;base64,.jpg');
% Convert to grayscale (if needed)
if size(image, 3) == 3
    image = rgb2gray(image);
end

% Add noise to the image (for demonstration purposes)
noisy_image = imnoise(image, 'gaussian', 0, 0.01);

% Apply Gaussian Filter
gaussian_filtered = imgaussfilt(noisy_image, 2);

% Apply Median Filter
median_filtered = medfilt2(noisy_image, [3, 3]);

% Apply Bilateral Filter using edge-preserving filter
bilateral_filtered = imguidedfilter(noisy_image);

% Display the images
figure;
subplot(2, 3, 1);
imshow(image);
title('Original Image');

subplot(2, 3, 2);
imshow(noisy_image);
title('Noisy Image');

subplot(2, 3, 3);
imshow(gaussian_filtered);
title('Gaussian Filter');

subplot(2, 3, 4);
imshow(median_filtered);
title('Median Filter');

subplot(2, 3, 5);
imshow(bilateral_filtered);
title('Bilateral Filter');